import React, { Component } from 'react'
import Table from '../table/index.js'

class Home extends Component {


    state = {
        index : [],
        start : 0,
        end : 50,
        clickSortAssending : false
    }


    componentDidMount(){
        fetch(`http://localhost:3000/api/v1/posts`)
            .then(res => res.json())
            .then(data => {
                this.setState({index : data})
            } )
    }

    postStringShortener = (string) => {
        if (string){
            return string.slice(0,29).concat('...')
        } else {
            return 'no destriction'

        }
    }

    renderData = () => {
        if(this.state.index.length > 0){ 
                return <Table data={this.state.index} clickSortHandler={this.clickSortHandler} /> 
        }
    }

    nextButtonHandler = () => {
        this.setState({
            start : this.state.start += 50,
            end : this.state.end += 50
        })
    }

    backButtonHandler = () => {
        if (this.state.start > 0){
            this.setState({
                start : this.state.start -= 50,
                end : this.state.end -= 50
            })
        }
    }

    clickSortHandler = () => {
        let sortedArray = this.state.index.sort(function(a,b){
            return a.clicks-b.clicks
        })
        this.setState({
            index : sortedArray,
            clickSortAssending : !this.state.clickSortAssending,
            start : 0,
            end : 50
        })


    }


    render(){
        console.log('render', this,state.index)
        return(
            <div>
                {this.renderData()}

        </div>
        )
    }
}

export default Home
