import React, { Component } from 'react'

class Home extends Component {


    state = {
        index : []
    }


    componentDidMount(){
        fetch(`http://localhost:3000/api/v1/posts`)
            .then(res => res.json())
            .then(data => {
                this.setState({index : data})
            } )
    }

    postStringShortener = (string) => {
        if (string.length > 30){
            return string.slice(0,29)
        } else {
            return string
        }
    }

    renderData = () => {
        if(this.state.index.length > 0){
            return this.state.index.map(post => <p>{post.id} , {post.text}</p>)
        }
    }



    render(){
        return(
            <div>
                <h1>
                    Home
                </h1>
                {this.renderData()}

        </div>
        )
    }
}

export default Home
