import React, { Component } from 'react'

let page = 1

class Home extends Component {


    state = {
        index : []
    }


    componentDidMount(){
        fetch(`http://localhost:3000/api/v1/posts/?_limit=10&_page=${page}`)
            .then(res => res.json())
            .then(data => {
                this.setState({index : data})
            } )
    }


    renderPosts = () => {
        if (this.state.index){
            this.state.index.forEach((post) => {
                return <p>{post}</p>
            })
        } 
    }

    render(){
        console.log(this.state, page)
        return(
            <div>
                {this.state.index ? renderPosts() : null}
        </div>
        )
    }
}

export default Home
