import React ,{ Component } from 'react'
import { Select } from 'semantic-ui-react'

const types = []

const allTypes = {}

class FilterSelect extends Component{

    state = {
        targetFilter : ''
    }

    collectTargetTypes = () => {
        if(this.props.data){
            this.props.data.map((post)=> {
                Object.entries(post.targeting).map((item)=>{
                    if(item[1]){
                        Object.entries(item[1]).map((value)=>{
                            value.map((a)=>{
                                if(allTypes[`${item[0]}`]){
                                    if(!allTypes[`${item[0]}`].includes(a)){
                                        allTypes[`${item[0]}`].push(a)
                                    }
                                }
                                else{
                                    allTypes[`${item[0]}`]=[]
                                }
                            }) 
                        })
                    }
                })  
            })
        }
    }

    assignKeys = () => {
        this.collectTargetTypes()
        Object.keys(allTypes).map((attribute)=>{
            types.push({key :attribute, text: attribute.replace(/_/g,' '), value : attribute})
        })
    }

    onChangeHandle = (e) =>{
        this.setState({
            targetFilter : e.target.innerText.replace(/ /g, '_')
        })
    }

    render(){
        this.assignKeys()
        console.log(this.state)
        return(
            <Select compact options={types} defaultValue='articles' onChange={(e)=> this.onChangeHandle(e)}/>
        )
    }
}

export default FilterSelect
