import React, { Component } from 'react'

let rows = []

class PostTable extends Component {


    state = {
        direction : '',
        data : []
    }


    sortHandler = (e) => {
        let data = this.props.data

        switch(e.target.id){
            case 'none':
                rows = data.sort((a,b)=>{
                    return a.clicks-b.clicks
                })
                e.target.id = 'Assc'
                this.setState({ direction : 'Assc' })
                break;
            case 'Assc': 
                rows = data.sort((a,b)=>{
                    return b.clicks-a.clicks
                })
                e.target.id = 'Dscd'
                this.setState({ direction : 'Dscd' })
                break;
            case 'Dscd':
                rows = data
                console.log(data)
                e.target.id = 'none'
                this.setState({direction : 'none'})
                break;
            default:
                return null
        }
    }


    postStringShortener = (string) => {
        if(string && string.length > 50){
            return string.slice(0,50).concat('...')
        }
        else if(string && string.length < 50){
            return string
        }
        else 
            return 'no description'
    }

    getTableRows = () => {
            return rows.map(post => <tr>
                <td>{post.id}</td>
                <td>{this.postStringShortener(post.text)}</td>
                <td>{post.clicks}</td>
                <td>{post.impressions}</td>
                <td>{`${post.spend.amount ? post.spend.amount : 0} ${post.spend.currency ? post.spend.currency : ''}`}</td>
                </tr>)

        }

    collectRows = () => {
        if(rows.length <= 0){
            this.props.data.map(post => rows.push(post))
        }
    }

    render() {
        this.collectRows()
        return(
            <div className='table'>
            <table style={{width : '95vw'}}>
                <tr>
                <th>Post ID</th>
                <th>Text</th>
                <th><button
                    name='clicks'
                    id='none'
                    onClick={this.sortHandler}>Clicks</button></th>
                <th><button>Impressions</button></th>
                <th>Currency</th>
            </tr>
            {this.getTableRows()}
        </table>
</div>
    )
  }
}

export default PostTable
