import React ,{ Component } from 'react'
import { Select } from 'semantic-ui-react'

const types = []

const allTypes = {}

class FilterSelect extends Component{

    collectTargetTypes = () => {
        if(this.props.data){
            this.props.data.map((post)=> {
                Object.entries(post.targeting).map((item)=>{
                    if(item[1]){
                        Object.entries(item[1]).map((value)=>{
                            value.map((a)=>{
                                if(typeof(a)==='object'){
                                    a.map((b)=>{
                                        if(allTypes[`${item[0]}`]){
                                            if(!allTypes[`${item[0]}`].includes(b)){
                                                allTypes[`${item[0]}`].push(b)
                                        }
                                    }
                                    else{
                                        allTypes[`${item[0]}`]=[]
                                    }
                                    })
                                }
                                    if(allTypes[`${item[0]}`]){
                                        if(!allTypes[`${item[0]}`].includes(a)){
                                            allTypes[`${item[0]}`].push(a)
                                        }
                                    }
                                    else{
                                        allTypes[`${item[0]}`]=[]
                                    }
                                }) 
                        })

                    }
                })  
            })
        }
        console.log(allTypes.location.flatten())
    }

    assignKeys = () => {
        this.collectTargetTypes()
    }



    render(){
        this.assignKeys()
        return(
            <Select compact options={''} defaultValue='articles' />
        )
    }
}

export default FilterSelect
