import React, { Component } from 'react';

class Table extends Component{

    postStringShortener = (string) => {
        if (string){
            return string.slice(0,29).concat('...')
        } else {
            return 'no destriction'

        }
    }

    getRows = () => {
        this.props.data.map((post)=>{
            return (
                <tr>
                    <td>{post.id}</td>
                    <td>{post.clicks}</td>
                    <td>post.impressions</td>
                    <td>{this.postStringShortener(post.text)}</td>
            </tr>
            )
        })
    }


    render(){
    console.log('inside of the table', this.props.data)

        return(
            <div>
                <table>
                    <tr>
                        <th>Post ID</th>
                        <th>Clicks</th>
                        <th>Impressions</th>
                        <th>Currency</th>
                    </tr>
                        {this.getRows()}
            </table>

        </div>
        )
    }
}


export default Table
