import React, { Component } from 'react'

class PostTable extends Component {


    state = {
        direction : 'Assc'
    }



    sortHandler = () => {

    }


    postStringShortener = (string) => {
        if(string && string.length > 50){
            return string.slice(0,50).concat('...')
        }
        else if(string && string.length < 50){
            return string
        }
        else 
            return 'no description'
    }

    getTableRows = (data) => {
        return data.map(post => <tr>
            <td>{post.id}</td>
            <td>{this.postStringShortener(post.text)}</td>
            <td>{post.clicks}</td>
            <td>{post.impressions}</td>
            <td>{`${post.spend.amount ? post.spend.amount : 0} ${post.spend.currency ? post.spend.currency : ''}`}</td>
            </tr>)
    }
    render() {
        return(
            <div className='table'>
            <table style={{width : '95vw'}}>
                <tr>
                <th>Post ID</th>
                <th>Text</th>
                <th><button>Clicks</button></th>
                <th><button>Impressions</button></th>
                <th>Currency</th>
            </tr>
            {this.getTableRows(this.props.data)}
        </table>
</div>
    )
  }
}

export default PostTable
